sub ${var} {
    my $$self = shift;
    my ($$${var}) = @_;
    return $$self->{'${var}'} unless (defined($$${var}));
    $$self->{'${var}'} = $$${var};
}

sub new {
    my $$class = shift;
    my %args  = @_;
    my $$self  = bless {}, $$class;
    return $$self;
}

