sub start_${Element} { shift->collectCharacters(1); } ## no critic (RequireArgUnpacking)
# xml tag </${Element}> - fetch characters
sub end_${Element} {
    my $$self = shift;
    my $$Obj = $$self->topID('${Parent}');
    $$Obj->${Element}($$self->getCharactersEnd());
}

sub push${var} {
    my $$self = shift;
    my ($$${var}) = @_;
    push @{$$self->{'${var}List'}}, $$${var};
}
    
sub pop${var} { 
    my $$self = shift;
    return pop @{$$self->{'${var}List'}};
}

sub ${var}List {
    my $$self = shift;
    return $$self->{'${var}List'}//[];
}

