package Training::Polling::API::Object::PollingResult;
use base DE_EPAGES::Object::API::Object::Object;

use strict;

1;

sub default {
    my $$class = shift;
    my ($$Parent, $$hInfo) = @_;
    return {
        %{$$class->SUPER::default($$Parent, $$hInfo)},
        'PollingA'  => 1,
        'PollingB'  => 2,
        'PollingC'  => 3,
    };
}

package Training::Polling::UI::Shop;
use base DE_EPAGES::Presentation::UI::Object;

use strict;

use Training::Polling::API::PollingResult qw ( GetOrCreatePollingResult );

sub SavePolling {
    my $$self = shift;      #reference to self is 1st argument
    my ($$Servlet) = @_;    #Servlet is always second argument
    my $$Shop = $$Servlet->object;                #get object sent in URL
    my $$Result = $$Servlet->form->value('Result');        #get 'Result' value
    return unless $$Result =~ /^[ABC]$$/;         #validate 'Result'
    my $$Attribute = 'Polling' . $$Result;        #concatenate
    # create PollingResult object if missing
    my $$PollingResult = GetOrCreatePollingResult($$Shop);
    my $$OldValue  = $$PollingResult->get($$Attribute);     #get existing value
    $$PollingResult->set( { $$Attribute => $$OldValue + 1 } );
    return;
}

1;

sub ManagePolling {
	my $$self = shift; # remove self from arguments
	my ($$Servlet) = @_; # get servlet object from args
	my $$Shop = $$Servlet->object; # get shop object 
	my $$Form = $$Servlet->form; # get form object
 # read form values for polling attributes
	my $$PollingA = $$Form->value('PollingA');
	return unless $$PollingA =~ /^[0-9]+$$/;
 	my $$PollingB = $$Form->value('PollingB');
 	return unless $$PollingB =~ /^[0-9]+$$/;
	my $$PollingC = $$Form->value('PollingC');
	return unless $$PollingC =~ /^[0-9]+$$/;

    # get polling object
    use Training::Polling::API::PollingResult qw( GetOrCreatePollingResult );
    my $$PollingResult = GetOrCreatePollingResult($$Shop);

    # set polling attributes
    $$PollingResult->set({
        'PollingA'=>$$PollingA,
        'PollingB'=>$$PollingB,
        'PollingC'=>$$PollingC,
    });
    return;
}

