#========================================================================================
# §function     GetOrCreatePollingResult
# §state        public
#----------------------------------------------------------------------------------------
# §syntax       GetOrCreatePollingResult( $$Shop );
#----------------------------------------------------------------------------------------
# §description  Returns PollingResult, creates a new one if necessary
#----------------------------------------------------------------------------------------
# §input        $$Shop | Shop object | object
# §return       $$PollingResult | PollingResult object | object
#========================================================================================
sub GetOrCreatePollingResult {
    my ($$Shop) = @_;
    TestObject('Shop', $$Shop, 'DE_EPAGES::Shop::API::Object::Shop');
    return $$Shop->insertChild('PollingResult',{'Alias' => 'PollingResult'}) unless ($$Shop->existsChild('PollingResult'));
    return $$Shop->child('PollingResult');
}

