# §state        private
# §description  Attribute interface for PollingResult.
#========================================================================================
package Training::Polling::API::Attributes::PollingResult;
use base DE_EPAGES::Object::API::BaseAttribute;

use strict;

sub getAttributes {
    shift;
    my ($$Object, $$aNames) = @_;
    my $$hInfo = {};
    foreach my $$Name (@$$aNames) {
        if ($$Name eq 'TotalVotes') {
             $$hInfo->{$$Name} = $$Object->get('PollingA') + $$Object->get('PollingB') + $$Object->get('PollingC');
        }
    }
    return $$hInfo;
}

1;

