use strict;
use DE_EPAGES::Core::API::Error qw ( ExistsError GetError );
use DE_EPAGES::Core::API::Script qw ( RunScript );
use DE_EPAGES::Core::API::Warning qw ( Warning );
use DE_EPAGES::Database::API::Connection qw ( RunOnStore );
use DE_EPAGES::WebInterface::API::MessageCenter qw ( SynchronizeCache );
use Getopt::Long;
use Time::HiRes qw ( time );

sub Main {

    local $$| = 1;

    my $$Password;
    my $$StoreName;
    my $$Help;
    my $$NoOutput;

    GetOptions(
        'help'        => \$$Help,
        'nooutput'    => \$$NoOutput,
        'passwd=s'    => \$$Password,
        'storename=s' => \$$StoreName,
    );

    usage() if $$Help;

    my $$time1 = time;

    RunOnStore(
        Store      => $$StoreName,
        DBPassword => $$Password,
        Sub        => sub {
            ${RunMethod}();
        }
    );
    my $$time2   = time;
    my $$seconds = $$time2 - $$time1;
    my $$logmsg  = sprintf( 'need %.3f seconds', $$seconds );
    print $$logmsg. "\n" unless $$NoOutput;

    # you need SynchronizeCache if you have manipulated any
    # objects during the runtime of this script.
    eval { SynchronizeCache(); };
    Warning( GetError() ) if ExistsError();
}

sub ${RunMethod} {
    my %Arguments = @_;
    ${cursor}
    return;
}

sub usage {
    print <<END_USAGE;
Usage:
    perl $$0 [options] [flags]

options:
    -passwd       database user password              ()
    -storename    name of store                       (Store)

flags:
    -nooutput   do not print any output
    -help       show the command line options

Example:
    perl $$0 -storename Store
END_USAGE
    exit 2;
}

RunScript( 'Sub' => \&Main );

#========================================================================================
# §package      ${Package}::API::TLE::${TLEHandler}
# §state        public
# §description  TLEHandler '${TLEHandler}'. Respoonsible for TLE function ${MyTLE}
#========================================================================================
package ${Package}::API::TLE::${TLEHandler};

use strict;

sub new {
    my $$class = shift;
    my %args  = @_;
    my $$self  = bless {}, $$class;
    return $$self;
}

sub ${MyTLE} {
    my $$self = shift; 
    my ($$Processor, $$aParams) = @_; 

    ${cursor}
}

sub RegisterHandlerProc { 
    my ($$Params) = @_; 
    __PACKAGE__->new()->register( $$Params->{'Processor'} );
    return; 
} 

sub register { 
    my $$self = shift; 
    my ($$Processor) = @_; 
    $$Processor->registerHandler('FunctionHandler', $$self, '${MyTLE}');
    return; 
} 

1;

sub ManagePolling {
	my $$self = shift; # remove self from arguments
	my ($$Servlet) = @_; # get servlet object from args
	my $$Shop = $$Servlet->object; # get shop object 
	my $$Form = $$Servlet->form; # get form object
 # read form values for polling attributes
	my $$PollingA = $$Form->value('PollingA');
	return unless $$PollingA =~ /^[0-9]+$$/;
 	my $$PollingB = $$Form->value('PollingB');
 	return unless $$PollingB =~ /^[0-9]+$$/;
	my $$PollingC = $$Form->value('PollingC');
	return unless $$PollingC =~ /^[0-9]+$$/;

    # get polling object
    use Training::Polling::API::PollingResult qw( GetOrCreatePollingResult );
    my $$PollingResult = GetOrCreatePollingResult($$Shop);

    # set polling attributes
    $$PollingResult->set({
        'PollingA'=>$$PollingA // 0,
        'PollingB'=>$$PollingB // 0,
        'PollingC'=>$$PollingC // 0,
    });
    return;
}

