package Training::MyEvents::Hooks::ExportCustomer;
use base 'Exporter';

use strict;

use DE_EPAGES::Core::API::File qw ( ConvertPathChars WriteFile );
use DE_EPAGES::Core::API::String qw ( QuoteXML );

sub OnUpdateAddress {
    my ($$hParams) = @_;

    my $$Address = $$hParams->{'Object'};
    return unless $$Address->parent->instanceOf('Customer');

    my $$FirstName = QuoteXML( $$Address->get('FirstName') );
    my $$LastName  = QuoteXML( $$Address->get('LastName') );
    my $$Alias     = QuoteXML( $$Address->alias );              # other atts, too...
    my $$Content   = <<EOL;
<?xml version="1.0"?>
<users>
  <user>
    <id>$$Alias</id>
    <first_name>$$FirstName</first_name>
    <last_name>$$LastName</last_name><!-- more att tags... -->
  </user>
</users>
EOL

    WriteFile( $$ENV{EPAGES_WEBROOT} . '/TrainingExport/' . 'Epages_updated_customer_' . ConvertPathChars($$Alias) . '.xml',
        \$$Content, { create_directory => 1 } );

}

1;

